import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { FOOD } from 'src/app/core/Models/food';
import { FoodApiService } from 'src/app/core/services/food/food-api.service';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit{
  foodItems:FOOD[]=[];
  myFood:FormGroup;
  foodObject:any=[];
  constructor(private fApiService:FoodApiService,private fBuilder:FormBuilder){
    this.myFood=this.fBuilder.group({
      food_name:[''],
      food_desc:[''],
      food_price:[''],
      food_image:[]
    })
  }
  ngOnInit(): void {
    //call get all foods items
    this.getAllFood();
  }
  // set image file
  setFile(Event:any){
    let file=Event.target.files[0];
    console.log(file);
    // set file into myfood
    this.myFood.get('food_image')?.setValue(file);

  }
  // get all foods items
  getAllFood(){
    this.fApiService.getAllFoods().subscribe({
      next:((res:any)=>{
        // console.log(res);
        this.foodItems=res;
        console.log(this.foodItems);
      }),
      error:((error)=>{
        console.log(error);
      })
    })
  }
  // add new food Items
  addFood(){
    // console.log(this.myFood.value);
    let foodFormData=new FormData();
    let formData=this.myFood.value;
    // foodFormData.append('food_name',this.myFood.get('food_name')?.value);
    // foodFormData.append('food_desc',this.myFood.get('food_desc')?.value);
    // foodFormData.append('food_price',this.myFood.get('food_price')?.value);
    // foodFormData.append('food_image',this.myFood.get('food_image')?.value);
    Object.keys(formData).forEach((keys:any)=>{
      foodFormData.append(keys,formData[keys])
    });
    // console.log(foodFormData);
    // add food api call
    this.fApiService.addNewFood(foodFormData).subscribe({
      next:((res:any)=>{
        console.log(res);
        alert(res.message);
        // get all food
        this.getAllFood();
        // Form reset
        this.myFood.reset();
      }),
      error:((error)=>{
        console.log(error);
      })
    });
  }
//set value myFood
  myFoodSet(f:any){
    console.log(f);
    this.foodObject=f;
    // now setting up the value into myfood form
    this.myFood.get('food_name')?.setValue(this.foodObject.food_name);
    this.myFood.get('food_desc')?.setValue(this.foodObject.food_desc);
    this.myFood.get('food_price')?.setValue(this.foodObject.food_price);

  }
  // update food item
  update(){
    let foodFormData=new FormData();
    let formData=this.myFood.value;
    // foodFormData.append('food_name',this.myFood.get('food_name')?.value);
    // foodFormData.append('food_desc',this.myFood.get('food_desc')?.value);
    // foodFormData.append('food_price',this.myFood.get('food_price')?.value);
    // foodFormData.append('food_image',this.myFood.get('food_image')?.value);
    Object.keys(formData).forEach((keys:any)=>{
      foodFormData.append(keys,formData[keys])
    });
    // console.log(foodFormData);
    // update api call
    this.fApiService.updateFood(this.foodObject._id,foodFormData).subscribe({
      next:((res:any)=>{
        console.log(res);
        // get call food items
        this.getAllFood();
        // form reset
      }),
      error:((error)=>{
        console.log(error);
      })
    })
  }

}
<header class="jumbotron text-center">
    <h5>Admin Can Perform all the operations</h5>
</header>
<div class="container-fluid">
    <h2>Food related operations...</h2>
    <p>Admin can add new food,update,delete,view all foods items
        <button data-toggle="modal" data-target="#exampleModal" class="btn btn-sm btn-success float-right">Add new food</button>
    </p>
    <table class="table table-hover">
        <thead>
          <tr>
            <th>Sl No</th>
            <th>Food</th>
            <th>Desc</th>
            <th>Price</th>
            <th>Image</th>
            <th>Action</th>

          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let f of foodItems; let i=index">
            <th>{{i+1}}</th>
            <td>{{f.food_name}}</td>
            <td>{{f.food_desc}}</td>
            <td>{{f.food_price}}</td>
            <td><img width="68px" height="68px" src="{{f.food_image}}" alt="{{f.food_name}}" title="{{f.food_name}}"></td>
            <td>
                <button class="btn btn-sm btn-outline-warning" data-toggle="modal" data-target="#exampleModal" (click)="myFoodSet(f)">Update</button>
                <button class="btn btn-sm btn-outline-danger">Delete</button>
            </td>
          </tr>

        </tbody>
      </table>



  <!-- add Modal -->
  <!-- Modal -->
  <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
    <div class="modal-dialog" role="document">
      <div class="modal-content">
        <div class="modal-header">
          <h5 class="modal-title" id="exampleModalLabel">Food operations</h5>
          <button type="button" class="close" data-dismiss="modal" aria-label="Close">
            <span aria-hidden="true">&times;</span>
          </button>
        </div>
        <div class="modal-body">
            <form [formGroup]="myFood">
            <div class="form-group">
               <label>Food:</label>
               <input type="text" formControlName="food_name" class="form-control"> 
            </div>
            <div class="form-group">
                <label>Desc:</label>
                <textarea rows="10" cols="20" formControlName="food_desc" class="form-control"></textarea>
             </div>
             <div class="form-group">
                <label>Price:</label>
                <input type="number" formControlName="food_price" class="form-control"> 
             </div>
             <div class="form-group">
                <label>Image:</label>
                <input type="file" (change)="setFile($event)" class="form-control"> 
             </div>
            </form>
        </div>
        <div class="modal-footer">
          <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
          <ng-container *ngIf="foodObject!=0 else AddButton">
            <button type="button" class="btn btn-info" >Update</button>
          </ng-container>
          <ng-template #AddButton>
            <button type="button" class="btn btn-success" (click)="addFood()">Add food</button>
          </ng-template>

        </div>
      </div>
    </div>
  </div>
</div>
