import { Component, OnInit } from '@angular/core';
import { FOOD } from 'src/app/core/Models/food';
import { FoodApiService } from 'src/app/core/services/food/food-api.service';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit{
  foodItems:FOOD[]=[];
  constructor(private fApiService:FoodApiService){

  }
  ngOnInit(): void {
    //call get all foods items
    this.getAllFood();
  }
  // get all foods items
  getAllFood(){
    this.fApiService.getAllFoods().subscribe({
      next:((res:any)=>{
        // console.log(res);
        this.foodItems=res;
        console.log(this.foodItems);
      }),
      error:((error)=>{
        console.log(error);
      })
    })
  }
}
<header class="jumbotron text-center">
    <h5>Admin Can Perform all the operations</h5>
</header>
<div class="container-fluid">
    <h2>Food related operations...</h2>
    <p>Admin can add new food,update,delete,view all foods items</p>
    <table class="table table-hover">
        <thead>
          <tr>
            <th>Sl No</th>
            <th>Food</th>
            <th>Desc</th>
            <th>Price</th>
            <th>Image</th>
            <th>Action</th>

          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let f of foodItems; let i=index">
            <th>{{i+1}}</th>
            <td>{{f.food_name}}</td>
            <td>{{f.food_desc}}</td>
            <td>{{f.food_price}}</td>
            <td><img width="68px" height="68px" src="{{f.food_image}}" alt="{{f.food_name}}" title="{{f.food_name}}"></td>
            <td>
                <button></button>
            </td>
          </tr>

        </tbody>
      </table>
</div>
