import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { FOOD } from 'src/app/core/Models/food';
import { FoodApiService } from 'src/app/core/services/food/food-api.service';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit{
  foodItems:FOOD[]=[];
  myFood:FormGroup
  constructor(private fApiService:FoodApiService,private fBuilder:FormBuilder){
    this.myFood=this.fBuilder.group({
      food_name:['Rice'],
      food_desc:['rice'],
      food_price:['100'],
      food_image:[]
    })
  }
  ngOnInit(): void {
    //call get all foods items
    this.getAllFood();
  }
  // set image file
  setFile(Event:any){
    let file=Event.target.files[0];
    console.log(file);
    // set file into myfood
    this.myFood.get('food_image')?.setValue(file);

  }
  // get all foods items
  getAllFood(){
    this.fApiService.getAllFoods().subscribe({
      next:((res:any)=>{
        // console.log(res);
        this.foodItems=res;
        console.log(this.foodItems);
      }),
      error:((error)=>{
        console.log(error);
      })
    })
  }
}
